mapper.select:
select	c.*, 
		pd.*
from	cart c, 
		product_detail pd
where	pd.product_detail_no = c.product_detail_no 
	and email = #{email}

select	product_detail_no 
from	product_detail 
where	product_no = #{productNo}
		and product_size = #{size}
		and color = #{color}

select	nvl(count(cart_no),0) cartNo 
from	cart 
where	email = #{email}
	and product_detail_no = #{productDetailNo}

mapper.update:
update	cart 
set		cart_quantity = cart_quantity+1 
where	email = #{email}
	and product_detail_no = #{productDetailNo}
