mapper.select:
select 	* 
from 	(select a.*, rowNum rn from (select * from notice order by notice_no desc)a) 
where 	rn between #{startRow} and #{endRow}

mapper.update:
update 	notice 
set 	notice_type = #{noticeType}, 
		notice_subject = #{noticeSubject}, 
		content = #{content},
		del = #{del} 
where 	notice_no = #{noticeNo}
