mapper.select:
select	r.*,
		p.product_name
from	review r,
		product p
where	p.product_no = r.product_no
	and r.email = #{email}

select	*
from	review
where	product_no = #{productNo}
	and email = #{email}
	and color = #{color}

mapper.update:
update	review
set		review_del = 'Y'
where	review_no = #{reviewNo}
