mapper.select:
select	*
from	review
where	product_no = #{productNo}
	and email = #{email}
	and color = #{color}

mapper.update:
update	review
set		review_del = 'Y'
where	review_no = #{reviewNo}

update	review
set		star = #{star},
		content = #{content}
where	review_no = #{reviewNo}

mapper.select:
select	r.*,
		p.product_name
from	review r,
		product p
where	p.product_no = r.product_no
	and r.email = #{email}

select 	* 
from 	(select a.*, rowNum rn from (select * from review 


				${keyword} like '%' || #{search} || '%'


order by review_no desc)a) 
where 	rn between #{startRow} 
	and #{endRow}

mapper.update:
update 	review 
set 	review_del = 'Y' 
where 	review_no = #{reviewNo}
